import React from "react";

// reactstrap components
import { Container, Button } from "reactstrap";
// core components

function LandingPage() {
  let pageHeader = React.createRef();

  React.useEffect(() => {
    if (window.innerWidth > 991) {
      const updateScroll = () => {
        let windowScrollTop = window.pageYOffset / 3;
        pageHeader.current.style.transform =
          "translate3d(0," + windowScrollTop + "px,0)";
      };
      window.addEventListener("scroll", updateScroll);
      return function cleanup() {
        window.removeEventListener("scroll", updateScroll);
      };
    }
  });

  return (
    <>
      <div className="page-header clear-filter">
        <div
          className="page-header-image"
          style={{
            backgroundImage:
              "url(" + require("assets/img/hero-1.jpg").default + ")",
          }}
          ref={pageHeader}
        ></div>
        <Container>
          <div className="content-center brand">
            <img
              alt="..."
              className="n-logo"
              src={require("assets/img/logoW.png").default}
            ></img>
            <h1 className="h1-seo">PLANNING <b><b>CAR</b></b></h1>
            <h3>Bienvenido a Planning Car, la aplicación web en la que podrás llevar el control de tu taller automotriz.</h3>
            <Button className="btn-round" color="info" href="/login">
            Ingresar{" "}
            <i class="fas fa-sign-in-alt"></i>
          </Button>
          </div>

        </Container>

      </div>
    </>
  );
}

export default LandingPage;
